library( ggplot2 )thickness <- read.csv( "LabelThickness.csv" )thickness2 <- read.csv( "LabelThickness2.csv" )startIndex <- grep( "X2002", colnames( thickness ) )endIndex <- length( colnames( thickness ) )spacingType <- c( 0.5, 0.6, 0.7, 0.8, 0.9, 1.0 )averageThickness <- c()spacing <- c()roiLabels <- c()for( i in seq( 0, 5 ) )  {  means <- colMeans( thickness[which( thickness$SPACING_TYPE == i ), startIndex:endIndex] )  averageThickness <- append( averageThickness, means )  spacing <- append( spacing, rep( spacingType[i+1], length( means ) ) )  roiLabels <- append( roiLabels, colnames( thickness )[startIndex:endIndex] )  }plotData <- data.frame( Spacing = spacing, RoiLabels = roiLabels, AverageThickness = averageThickness )plotData <- transform( plotData, RoiLabels = factor( RoiLabels ) );thickPlot <- ggplot( data = plotData, aes( x = Spacing, y = AverageThickness, colour = RoiLabels ) ) +             geom_line() +             scale_x_continuous( "Spacing (isotropic resampling)" ) +             scale_y_continuous( "Average Thickness (ATITH)" ) +             ggtitle( paste( "Kirby (LabelThickness2)", sep = "" ) )ggsave( filename = paste0( "~/Desktop/labelThickness2.pdf" ), plot = thickPlot, width = 8, height = 6, units = 'in' )